(4054, 58, 'content_details_2_title', 'Voices from the Other Side: Impressions from Some Igorot Participants in U.S. Cultural Exhibitions in the Early 1900s'),
(4055, 58, '_content_details_2_title', 'field_604b182bf1527'),
